import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [cards, setCards] = useState([]);
  // useEffect(() => {
  //   axios.get('https://api.pokemontcg.io/v2/cards?pageSize=10').then((result)=>{
  //     console.log(result.data);
