  //   const response =await fetch('https://api.pokemontcg.io/v2/cards');
  //   const body =await response.json();
  //   setCards(body.data);
  // }, [])

  //3 async outside useEffect 'recommended'
  useEffect(() => {
    getData();
  }, [])
  const getData= async()=>{
    const response = await fetch('https://api.pokemontcg.io/v2/cards');
    const body = await response.json();
    setCards(body.data)
  }

  return (
    <div className="App">
      {cards ? (
        <ul>
          {cards.map((item) => (
            <li>{item.name}</li>
          ))}
        </ul>
      ) : (
        <h1>Loading...</h1>
      )}
    </div>
  );
}

export default App;
